/**
 * 歌单的播放全部音乐按钮点击事件
 */
function onPlayAllClick(){
	//触发歌单歌曲列表的第一首音乐播放按钮点击事件，去播放第一首音乐
	//class为container-song，下面的class为row的第二行，属性为data-music-play的按钮button
	$(".container-song .row:eq(1) button[data-music-play]").click();
}


/**
 * 删除歌单按钮点击
 */
function onDeleteClick() {
	//弹出确认 提示框
	swal({
		title: "是否确认删除歌单？",
		icon: "warning",
		buttons: true,
		dangerMode: true
	}).then(function(willDelete) {
		if (willDelete) {
			//确认删除，则提交表单
			$("#delete-form").submit();
		}
	});
}

/**
 * 播放按钮点击事件
 */
$("[data-music-play]").click(function() {
	//获取要播放的音乐的id
	let id = $(this).attr("data-music-play");

	//调用播放方法
	musicPlay(id);
});

